const { RichEmbed } = require("discord.js");

const MENU =  [
	"Mac And Cheese",
	"Chicken Nuggets",
];

module.exports = {
	MENU: MENU,
	getMenu: function(user) {
		return new RichEmbed()
			.setColor("RED")
			.setTitle("Menu")
			.setDescription(MENU.join("\n"))
			.setFooter(`Ran by ${user.tag}.`, user.displayAvatarURL);
	}
}
